import math
import string

class FoxAndSightseeing:
    def getMin(self, position):
        minNum=1000000000;
        for i in range(1,len(position)-1):
            old=position[0]
            sum=0
            for j in range(1,len(position)):
                if (i!=j):
                    sum+=abs(old-position[j])
                    old=position[j]
            minNum=min(sum, minNum)
            
        return minNum;






